sp_widget.client_script:
function($scope) {
  /* widget controller */
  var c = this;

	$scope.myOnClick = function(id){
		//row.style.backgroundColor = "yellow";
		//alert(id);
		window.open(id, '_blank', 'location=yes,height=570,width=1020,scrollbars=yes,status=yes');
	};

}

